import { Link } from "react-router-dom/cjs/react-router-dom.min";

const ProductsList = ({ products, setProducts, setFilteredProducts, addToCart }) => {

    const handleDelete = (id) => {
        fetch('http://localhost:8000/products/' + id, {
            method: 'DELETE'
        }).then(() => {
            console.log('Product deleted');
            return fetch('http://localhost:8000/products');
        }).then(response => response.json())
            .then(data => {
                setProducts(data);
                setFilteredProducts(data);
            })
            .catch(error => console.error('Error deleting product:', error));
    }

    return (
        <div className="product-list mt-5">
            <div className="container">
                <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4 mb-5">
                    {products.map(product => (
                        <div className="col" key={product.id}>
                            <div
                                className="card h-100 border-3 p-1 shadow"
                                style={{ 
                                    borderColor: '#8A8B6F',  // Dark olive green
                                    transition: 'border-color 0.3s ease, transform 0.3s ease',
                                    backgroundColor: '#EDE6D1' // Off-white background
                                }}
                                onMouseOver={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
                                onMouseOut={(e) => e.currentTarget.style.transform = 'scale(1)'}
                                data-aos="fade-up"
                                data-aos-delay="200"
                            >
                                <Link to={`/products/productDetails/${product.id}`}>
                                    <img src={product.image} alt={product.name} className="card-img-top" />
                                </Link>
                                <div className="top-border" style={{ borderTop: '3px solid #8A8B6F', marginTop: '4px' }}></div>
                                <div className="card-body">
                                    <h4 className="card-title text-center mb-3" style={{ color: '#4D3621' }}>
                                        {product.name}
                                    </h4>
                                    <p className="card-text mb-0" style={{ color: '#6B695F' }}>Price: ${product.price}</p>
                                    <p className="card-text" style={{ color: '#6B695F' }}>Status: {product.availability ? 'Available' : 'Out of Stock'}</p>
                                </div>
                                <div className="card-footer d-flex justify-content-between">
                                    <div className="mybtns">
                                        <Link 
                                            type="button" 
                                            className="btn text-white" 
                                            style={{ backgroundColor: '#8A8B6F', borderColor: '#8A8B6F' }} 
                                            to={`/products/productDetails/${product.id}`}
                                        >
                                            View Details
                                        </Link>
                                        <button
                                            type="button"
                                            className="addToCartBtn btn text-white"
                                            onClick={() => addToCart(product)}
                                            disabled={!product.availability}
                                            style={{ backgroundColor: '#4D3621', borderColor: '#4D3621' }}
                                        >
                                            {product.availability ? 'Add to Cart' : 'Out of Stock'}
                                        </button>
                                    </div>

                                    <div className="action-icons d-flex align-items-center">
                                        <Link to={`/UpdateProduct/${product.id}`}>
                                            <i className="fas fa-edit" style={{ cursor: 'pointer', color: '#8A8B6F' }}></i>
                                        </Link>
                                        <i className="fas fa-trash" onClick={() => handleDelete(product.id)} style={{ color: '#8A8B6F', cursor: 'pointer', marginLeft: '10px' }}></i>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
                <div className="d-flex justify-content-end mb-5">
                    <Link
                        type="button"
                        className="addTopicBtn fw-bold btn rounded-pill shadow px-4 py-2"
                        style={{ backgroundColor: '#8A8B6F', color: "white", border: "none" }}
                        to="/AddProduct"
                    >
                        Add Product
                    </Link>
                </div>
            </div>
        </div>
    );
}

export default ProductsList;
