import { Link } from "react-router-dom/cjs/react-router-dom.min";

const DashProductList = ({ products, setProducts, setFilteredProducts, addToCart }) => {

    const handleDelete = (id) => {
        // Ask for confirmation before deleting
        const isConfirmed = window.confirm("Are you sure you want to delete this product?");
        if (isConfirmed) {
            fetch('http://localhost:8000/products/' + id, {
                method: 'DELETE'
            }).then(() => {
                console.log('Product deleted');
                return fetch('http://localhost:8000/products');
            }).then(response => response.json())
                .then(data => {
                    setProducts(data);
                    setFilteredProducts(data);
                })
                .catch(error => console.error('Error deleting product:', error));
        }
    }

    return (
        <div className="product-list mt-5">
            <div className="container">
                {products.length === 0 ? (
                    <div className="col-12 text-center">
                        <p>No products found!!</p>
                    </div>
                ) : (
                    <div className="row row-cols-1 row-cols-md-2 row-cols-lg-2 row-cols-xl-4 row-cols-xxl-5 g-4 py-5">
                        {products.map(product => (
                            <div className="col" key={product.id}>
                                <div className="card h-100 border-3 p-1 shadow">
                                    <div className="card-body text-center">
                                        <h3 className="mt-2 border rounded bg-success text-white pt-1 pb-1 fs-5">
                                            Rs.{product.price}
                                        </h3>
                                        <img src={product.image} className="container col-12 mt-3" alt={product.name} />
                                        <h4 className="card-title">{product.name}</h4>
                                        <p className="card-text">{product.category}</p>
                                        <p className="card-text">{product.brand}</p>
                                        <div className="d-flex align-items-center justify-content-center">
                                            <Link to={`/products/productDetails/${product.id}`} className="btn btn-sm btn-outline-warning me-1">
                                                <i className="bi bi-eye"></i>
                                            </Link>
                                            <Link to={`/UpdateProduct/${product.id}`} className="btn btn-sm btn-outline-primary me-1">
                                                <i className="bi bi-pencil-square"></i>
                                            </Link>
                                            <button
                                                onClick={() => handleDelete(product.id)}
                                                className="btn btn-sm btn-outline-danger"
                                            >
                                                <i className="bi bi-trash"></i>
                                            </button>
                                        </div>
                                        <div className="mt-3">
                                            {product.stock > 10 ? (
                                                <span className="badge bg-success">In Stock: {product.stock}</span>
                                            ) : product.stock > 0 && product.stock <= 10 ? (
                                                <span className="badge bg-warning">Low Stock: {product.stock}</span>
                                            ) : (
                                                <span className="badge bg-danger">Out of Stock</span>
                                            )}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
                <div className="d-flex justify-content-end mb-5">
                    <Link
                        type="button"
                        className="addTopicBtn fw-bold btn rounded-pill shadow px-4 py-2 "
                        style={{ color: "white", border: "none" }}
                        to="/AddProduct"
                    >
                        Add Product
                    </Link>
                </div>
            </div>
        </div>
    );
}

export default DashProductList;
