import React from 'react';
import { Link } from 'react-router-dom';
import '../index.css'; // Import external CSS file
import ProductList from './Products';
import About from './About';
import Contact from './Contact';

const Home = () => {
    return ( 
        <div className="home" style={{ backgroundColor: '#F7EED3' }}>
            {/* Hero Section */}
            <div className="hero-section mb-5" style={{ backgroundColor: '#4D3621', padding: '100px 0', textAlign: 'center' }}>
                <div className="hero-content text-center">
                    <h1 className="hero-title" style={{ color: '#F7EED3' }}>Welcome to Sweet Dreams Bakery</h1>
                    <p className="hero-subtitle" style={{ color: '#EDE6D1' }}>Where every dessert tells a story!</p>
                    <Link to="/products" className="btn btn-lg" style={{ backgroundColor: '#8A8B6F', color: '#F7EED3', borderColor: '#8A8B6F' }}>
                        Explore Our Products
                    </Link>
                </div>
            </div>

            {/* Product List */}
            <ProductList />

            {/* About Section */}
            <About />

            {/* Contact Section */}
            <Contact />
        </div>
    );
}

export default Home;
